declare namespace sys {
	/**
	 * @description 可能为 undefined 的 {@link T} 类型值。
	 * @author Wang Yucai
	 *
	 * @typedef {UndefinedType}
	 * @template T
	 */
	type UndefinedType<T> = T | undefined;
}
